mapper.select:
SELECT tbr.id,tbr.back_record_no, o.order_title, tbpd.back_periods , tbr.back_money, tbr.back_date, tbr.check_status, tbr.submit_time FROM t_back_record tbr LEFT JOIN t_order o ON tbr.order_id = o.id LEFT JOIN t_customer cus ON tbr.customer_id = cus.id LEFT JOIN t_back_plan_detail tbpd ON tbr.back_pland_id = tbpd.id AND tbr.back_id = tbpd.back_plan_id LEFT JOIN t_back_plan tbp ON tbr.back_id = tbp.id
AND tbr.check_status = #{backRecord.checkStatus}
AND tbr.submit_time = #{backRecord.submitTime}
ORDER BY tbr.id LIMIT #{current}, #{size}

SELECT tbr.id,tbr.back_record_no, o.order_title, tbpd.back_periods , tbr.back_money, tbr.back_date, tbr.check_status FROM t_back_record tbr LEFT JOIN t_order o ON tbr.order_id = o.id LEFT JOIN t_customer cus ON tbr.customer_id = cus.id LEFT JOIN t_back_plan_detail tbpd ON tbr.back_pland_id = tbpd.id AND tbr.back_id = tbpd.back_plan_id LEFT JOIN t_back_plan tbp ON tbr.back_id = tbp.id
AND tbr.check_status = #{checkStatus}
AND tbr.submit_time >= #{submitStartTime}
AND tbr.submit_time <= #{submitEndTime}
ORDER BY tbr.id LIMIT #{current}, #{size}

mapper.update:
UPDATE t_back_record
SET check_status = #{checkStatus}, remark = #{remark}, check_time = #{checkTime}
WHERE id = #{bid}
