mapper.select:
select c.customer_name,b.bussiness_title,s.bussiness_stage,b.price_records, b.last_follow, b.not_follow_days
from t_business b,t_customer c,t_business_stage s,t_business_origin o
where b.customer_id=c.id and b.bussiness_stage_id=s.id

    and c.customer_name like concat('%',#{customer},'%')


    and o.business_origin=#{businessOrigin}


    and s.business_stage=#{businessStage}


    and b.last_follow  >= #{startDate}


    and b.last_follow  <= #{endDate}

limit #{page},#{size}

select b.bussiness_title,s.bussiness_stage,o.bussiness_origin
from t_business b,t_business_stage s,t_business_origin o
where b.bussiness_stage_id=s.id and b.bussiness_orgin_id=o.id and id=#{id}
